function getWeatherData(t){let e=t.coords.latitude,n=t.coords.longitude,o=`https://api.openweathermap.org/data/2.5/weather?lat=${e}&lon=${n}&appid=12497211893d7775c0f55217bb796cd5&units=metric`;fetch(o).then(t=>t.json()).then(t=>{displayWeatherData(t)}).catch(t=>{console.error("Error fetching weather data:",t)})}function getLocationByCityName(t){}function displayWeatherData(t){let e=`${t.name}, ${t.sys.country}`,n=`Temperature: ${t.main.temp}\xb0C`,o=`Humidity: ${t.main.humidity}%`,a=`Wind Speed: ${t.wind.speed} m/s`,r=`Pressure: ${t.main.pressure} hPa`,i=`http://openweathermap.org/img/w/${t.weather[0].icon}.png`;document.getElementById("location").textContent=e,document.getElementById("weather-icon").src=i,document.getElementById("temperature").textContent=n,document.getElementById("humidity").textContent=o,document.getElementById("wind-speed").textContent=a,document.getElementById("pressure").textContent=r}function showError(t){console.error("Error getting geolocation:",t),getLocationByCityName("YourCityName")}window.addEventListener("load",()=>{navigator.geolocation?navigator.geolocation.getCurrentPosition(getWeatherData,showError):getLocationByCityName("YourCityName")});